from typing import List, Any
from loguru import logger
from langflow.custom import Component
from langflow.io import DataInput, Output
from langflow.schema import Data

class MergeDataComponent(Component):
    display_name = "Merge Data"
    description = "Combines multiple Data objects into a single unified Data object, preserving lists and handling duplicates."
    icon = "merge"
    beta = False

    inputs = [
        DataInput(
            name="data_inputs",
            display_name="Data Inputs",
            info="List of Data objects to be merged.",
            is_list=True,
        ),
    ]

    outputs = [
        Output(display_name="Merged Data", name="merged_data", method="merge_data"),
    ]

    def _merge_data(self, target: dict, source: Any, key: str) -> None:
        if key not in target:
            target[key] = source
        else:
            if isinstance(target[key], list):
                if isinstance(source, list):
                    target[key].extend(source)
                else:
                    target[key].append(source)
            elif isinstance(target[key], dict) and isinstance(source, dict):
                for k, v in source.items():
                    self._merge_data(target[key], v, k)
            else:
                target[key] = [target[key], source]

    def merge_data(self) -> Data:
        logger.info("Starting data merge process")
        data_inputs: List[Data] = self.data_inputs

        if not data_inputs:
            logger.warning("No data inputs provided")
            return Data()

        if len(data_inputs) == 1:
            logger.info("Only one data input provided, returning as is")
            return data_inputs[0]

        merged_data = {}
        for data in data_inputs:
            logger.debug(f"Merging data input")
            if not isinstance(data, Data):
                logger.error("Input is not a Data object")
                raise ValueError("Input is not a Data object")

            if data.data is None:
                continue

            if isinstance(data.data, dict):
                for key, value in data.data.items():
                    self._merge_data(merged_data, value, key)
            elif isinstance(data.data, list):
                self._merge_data(merged_data, data.data, 'text')
            else:
                self._merge_data(merged_data, data.data, 'text')

        logger.info("Data merge completed successfully")
        return Data(data=merged_data)

from langflow.custom import Component
from langflow.io import MessageTextInput, Output
from langflow.schema import Data
import csv
from io import StringIO
import re
import json
from typing import List, Dict, Any, Union

class MarkdownDataExtractorComponent(Component):
    display_name = "Extract Block from Markdown"
    description = "Extracts data from markdown code blocks and converts it to a structured format."
    documentation: str = "https://docs.langflow.org/components/custom"
    icon = "FileText"
    name = "MarkdownDataExtractorComponent"

    inputs = [
        MessageTextInput(name="input_value", display_name="Markdown Text", value=""),
        DropdownInput(name="block_type", display_name="Block Type (csv, json, yaml)", options=["csv", "json", "yaml"], value="csv"),
    ]

    outputs = [
        Output(display_name="Extracted Data", name="output", method="build_output"),
    ]

    def _extract_code_block(self, markdown_text: str, block_type: str) -> str:
        """Extract content from a specific type of code block in markdown text."""
        pattern = rf'```{block_type}\n(.*?)\n```'
        match = re.search(pattern, markdown_text, re.DOTALL)
        if not match:
            raise ValueError(f"No {block_type} content found between triple backticks.")
        return match.group(1)

    def _parse_csv(self, csv_string: str) -> List[Dict[str, Any]]:
        """Parse CSV string into a list of dictionaries."""
        csv_file = StringIO(csv_string)
        csv_reader = csv.DictReader(csv_file)
        return [row for row in csv_reader]

    def _parse_json(self, json_string: str) -> Union[Dict[str, Any], List[Dict[str, Any]]]:
        """Parse JSON string into a dictionary or list of dictionaries."""
        return json.loads(json_string)

    def _parse_yaml(self, yaml_string: str) -> Union[Dict[str, Any], List[Dict[str, Any]]]:
        """Parse YAML string into a dictionary or list of dictionaries."""
        import yaml
        return yaml.safe_load(yaml_string)

    def build_output(self) -> Union[List[Data], Data]:
        try:
            block_type = self.block_type.lower()

            if block_type not in ['csv', 'json', 'yaml']:
                raise ValueError("Invalid block_type. Must be 'csv', 'json', or 'yaml'.")

            extracted_content = self._extract_code_block(self.input_value, block_type)

            if block_type == 'csv':
                data = self._parse_csv(extracted_content)
            elif block_type == 'json':
                data = self._parse_json(extracted_content)
            elif block_type == 'yaml':
                data = self._parse_yaml(extracted_content)

            if isinstance(data, list):
                data_objects = [Data(**entry) for entry in data]
                self.status = data_objects
                return data_objects
            elif isinstance(data, dict):
                self.status = Data(**data)
                return self.status
            else:
                raise ValueError(f"Unexpected data format from {block_type} parsing.")

        except ValueError as ve:
            self.status = f"Error: {str(ve)}"
            return Data(error=str(ve))
        except json.JSONDecodeError as je:
            self.status = f"JSON Decode Error: {str(je)}"
            return Data(error=f"JSON Decode Error: {str(je)}")
        except yaml.YAMLError as ye:
            self.status = f"YAML Parse Error: {str(ye)}"
            return Data(error=f"YAML Parse Error: {str(ye)}")
        except Exception as e:
            self.status = f"Unexpected Error: {str(e)}"
            return Data(error=f"Unexpected Error: {str(e)}")

